$(document).ready(function() {
    "" !== Cookies.get("viewStyle") && ($(".products-list").attr("data-view", Cookies.get("viewStyle")).find(">ul>li").attr("class", $(".view-style #" + Cookies.get("viewStyle")).attr("data-col")),
    $(".view-style #" + Cookies.get("viewStyle")).addClass("active").siblings("span").removeClass("active"))
}),
$(function() {
    $(".top-category").length && $(".panel.panel-item.categories").addClass("acategory"),
    $(".view-style span").click(function() {
        $(".products-list").attr("data-view", $(this).attr("id")).find(">ul>li").attr("class", $(this).attr("data-col")),
        $(this).addClass("active").siblings("span").removeClass("active"),
        Cookies.set("viewStyle", $(this).attr("id"), {
            expires: 7
        })
    }),
    $(".up").click(function() {
        $("html,body").stop().animate({
            scrollTop: "0"
        }, 1e3)
    }),
    $(".category-banner-slider").appendTo(".products-list-container .products-left-column")
});
